import React, { Component } from "react";
import { Route, Switch, Link, withRouter } from "react-router-dom";
import logo from "./logo.svg";
import "./App.css";
import UserSignup from "./Components/UserSignup";
import UserLogin from "./Components/UserLogin";

class App extends Component {
  componentDidMount() {
    // this get requests gives me my user profile
    // I can change 'profile' to 'users', it will still work.
    const jwt = require("jsonwebtoken");
    const token = localStorage.getItem("user_token");
    const decoded = jwt.verify(token, "my_s3cr3t");
    console.log(
      "%c what is this",
      "background: #222; color: yellow",
      jwt,
      decoded
    );
    fetch("http://localhost:3000/api/v1/profile", {
      method: "GET",
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
      .then(r => r.json())
      .then(data =>
        console.log(
          "%c Authenticate to be authorized",
          "background: #222; color: #bada55",
          data
        )
      );
  }

  clickListener = () => {
    localStorage.removeItem("user_token");
    this.props.history.push("/");
  };

  render() {
    console.log(
      "%c local Storage info: ",
      "background: #222; color: aqua",
      localStorage,
      localStorage.getItem("user_token")
    );
    return (
      <div className="App">
        <Switch>
          <Route path="/signup" component={UserSignup} />
          <Route path="/login" component={UserLogin} />
        </Switch>
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
          <Link to="/signup">
            <button>Signup</button>
          </Link>
          <Link to="/login">
            <button>Login</button>
          </Link>
          <button onClick={this.clickListener}>Logout</button>
        </header>
      </div>
    );
  }
}

export default withRouter(App);
